let vtUnit="mL";function calculate(){let e=parseFloat(document.getElementById("rr").value)||0,t=parseFloat(document.getElementById("vt").value)||0,n=parseFloat(document.getElementById("pplat").value)||0,l=parseFloat(document.getElementById("pip").value)||0,c=parseFloat(document.getElementById("peep").value)||0,a=.098*e*("mL"===vtUnit?t/1e3:t)*(n-.5*(l-c));document.getElementById("result").textContent=a.toFixed(2);let o=document.getElementById("interpretation");a<12?(o.textContent="Low mechanical power",o.style.backgroundColor="#d4edda",o.style.color="#155724"):a<18?(o.textContent="Moderate mechanical power",o.style.backgroundColor="#fff3cd",o.style.color="#856404"):(o.textContent="High mechanical power",o.style.backgroundColor="#f8d7da",o.style.color="#721c24")}document.getElementById("btnML").addEventListener("click",(()=>{vtUnit="mL",document.getElementById("btnML").classList.add("active"),document.getElementById("btnL").classList.remove("active"),calculate()})),document.getElementById("btnL").addEventListener("click",(()=>{vtUnit="L",document.getElementById("btnL").classList.add("active"),document.getElementById("btnML").classList.remove("active"),calculate()})),document.querySelectorAll(".calc-input").forEach((e=>{e.addEventListener("input",calculate)}));